import Gerencianet from 'gn-api-sdk-typescript';
import options from '../../config';

const params = {
    brand: 'visa',
    total: 5000,
};

const gerencianet = Gerencianet(options);

gerencianet
    .getInstallments(params)
    .then((resposta: any) => {
        console.log(resposta);
    })
    .catch((error: any) => {
        console.log(error);
    })
    .done();
